import React, {Component} from 'react'
import firebase from "firebase";
import "firebase/storage";
import { useQRCode } from 'react-hook-qrcode';
import Link from 'next/link';
import QRCode from "qrcode.react";

class QR_sample extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data:[]
    }
    this.getFireData();

  }

  async getFireData(){
    const db = firebase.firestore();
    const doc = await db.collection('users').doc('001').get();
    console.log(JSON.stringify(doc.data()));
    let result = JSON.stringify(doc.data());
    result = "aiou";
    return result;
  }


  render(){
    return (
      <div>
          <h1>健康状態</h1>
          <div>
            <QRCode value= {this.getFireData()} />
          </div>
          <Link href="/other">
            <a>Back to Home</a>
          </Link>
      </div>
    );
  }


}
export default QR_sample;
