import React, {Component} from 'react'
import firebase from "firebase";
import "firebase/storage";
import { useQRCode } from 'react-hook-qrcode';
import Link from 'next/link';
import QRCode from "qrcode.react";

class QR_sample extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data:[]
    }
    this.getFireData();

  }

  getFireData(){
    let db = firebase.database();
    let ref = db.ref('BodyTemperature/');
    let self  = this;
    let Nday = 3;
    ref.orderByKey().limitToLast(1).on('value', (snapshot)=>{
        self.setState({
          data:snapshot.val()
        });
    });

  }

  getQRData(){
    let result = [];
    if (this.state.data == null || this.state.data.length == 0){
      return [<tr key="0"><th>NO DATA.</th></tr>];
    }
    for(let i in this.state.data){
      let Temp = this.state.data[i].T;
      let Num = this.state.data[i].N;
      let Day = this.state.data[i].C;
    }
    let Result = Temp + ',' + Num + ',' + Day;
    return Result;
  }


  render(){
    return (
      <div>
          <h1>健康状態</h1>
          <div>
            <QRCode value= {this.getQRData()} />
          </div>
          <Link href="/other">
            <a>Back to Home</a>
          </Link>
      </div>
    );
  }


}
export default QR_sample;
