import React, {Component} from 'react'
import firebase from "firebase";
import "firebase/storage";
import { useQRCode } from 'react-hook-qrcode';
import Link from 'next/link';
import QRCode from "qrcode.react";

class QR_sample extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data:"a"
    }
  }

  async GetFireData(){
    const db = firebase.firestore();
    const doc = await db.collection('users').doc('001').get();
    let Result = JSON.stringify(doc.data())
    this.setState({data: Result});
    console.log(this.state.data);
  }

  render(){
    if(this.state.data == "a"){
      this.GetFireData();
    }
    return (
      <div>
          <h1>{this.GetFireData()}</h1>
          <button>取得</button>
          <div>
            <QRCode value= {this.state.data} />
          </div>
          <Link href="/other">
            <a>Back to Home</a>
          </Link>
      </div>
    );
  }

}
export default QR_sample;
