import React, {Component} from 'react'
import firebase from "firebase";
import "firebase/storage";
import { useQRCode } from 'react-hook-qrcode';
import Link from 'next/link';

class QR_sample extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data:[]
    }
    this.getFireData();

  }

  getFireData(){
    let db = firebase.database();
    let ref = db.ref('BodyTemperature/');
    let self  = this;
    ref.orderByKey()
      .limitToLast(1)
      .on('value', (snapshot)=>{
        self.setState({
          data:snapshot.val()
        });
    });
    let Temp = this.state.data.T;
    let Num = this.state.data.N;
    let Day = this.state.data.C;

  }

  getQR(){
    const [inputRef] = useQRCode({
      text: '体温：',
      options: {
        level: 'M',
        margin: 7,
        scale: 1,
        width: 200,
        color: {
          dark: '#00bfff',
          light: '#ffffff',
        },
      },
    });
    const ref = {inputRef};
    return ref;
  }

  render(){
    return (
      <div>
          <h1>健康状態</h1>
          <canvas>
            {this.getQR()}
          </canvas>
          <Link href="/other">
            <a>Back to Home</a>
          </Link>
      </div>
    );
  }


}
export default QR_sample;
