import React, {Component} from 'react'
import firebase from "firebase";
import "firebase/storage";
import { useQRCode } from 'react-hook-qrcode';
import Link from 'next/link';
import QRCode from "qrcode.react";

class QR_sample extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data:[]
    }
    this.getFireData();

  }

  getFireData(){
    let db = firebase.database();
    let ref = db.ref('BodyTemperature/');
    let self  = this;
    ref.orderByKey()
      .limitToLast(1)
      .on('value', (snapshot)=>{
        self.setState({
          data:snapshot.val()
        });
    });
    let Temp = this.state.data.T;
    let Num = this.state.data.N;
    let Day = this.state.data.C;

  }

  // getQR(){
