  //       margin: 7,
  //       scale: 1,
  //       width: 200,
  //       color: {
  //         dark: '#00bfff',
  //         light: '#ffffff',
  //       },
  //     },
  //   });
  //   const ref = {inputRef};
  //   return ref;
  // }

  render(){
    return (
      <div>
          <h1>健康状態</h1>
          // <canvas>
          //   {this.getQR()}
          // </canvas>
          <div>
            <QRCode value="https://google.com" />
          </div>
          <Link href="/other">
            <a>Back to Home</a>
          </Link>
      </div>
    );
  }


}
export default QR_sample;
