import React from 'react';
import { useQRCode } from 'react-hook-qrcode';
import { useState } from 'react';

const Sample = () => {

    const db = firebase.firestore();
    const doc = db.collection('users').doc('001').get();
    let Result = JSON.stringify(doc.data());
    const [inputRef] = useQRCode({
    text: this.getFireData(),
    options: {
      level: 'M',
      margin: 7,
      scale: 1,
      width: 200,
      color: {
        dark: '#3FB811',
        light: '#DDDDDD',
      },
    },
  });
  return (
    <div>
      <canvas ref={inputRef} />
    </div>
  );
}

export default Sample;
